import logo from './logo.svg';
import './App.css';
import APIClient from './apiClient'
import React from 'react'

let api = new APIClient("accessToken");

function App() {
  const [generations, setGenerations] = React.useState(null)
  React.useEffect(() => {
    const fetchData = async () => {
      const result = await api.generations();
      setGenerations(result.data);
    }
    fetchData();
  }, [])

  return (
    <div className="App">
      <Generations generations={generations} />
      <ComposerSettings />
    </div>
  );
}

const Generations = (props) => {
  const deleteItem = (id) => {
    console.log("deleting", id)
    console.log("deletion results: ",api.deleteGeneration(id))
  }

  return !props.generations || props.generations.map((item, index) => {
    return (
      <div key={index}>
        {item.composer} | {item.id} <button onClick={() => deleteItem(item.id)}>X</button>
      </div>);
  })
}

const ComposerSettings = (props) => {
  const [value, setValue] = React.useState("mozart");
  const handleChange = (event) => {
    setValue(event.target.value);
  };

  return (
    <select
      value={value}
      onChange={handleChange}
      >
      <option value={"mozart"}>Mozart</option>
      <option value={"chopin"}>Chopin</option>
    </select>
  );
}

export default App;
