import APIClient from './apiClient'
import React from 'react'
import {options} from "Selection/MuseNetOptions"
import {InputSelector} from "Selection/Inputs"

let api = new APIClient("accessToken");

function App() {
  const [generations, setGenerations] = React.useState(null)
  const fetchData = async () => {
    const result = await api.generations();
    setGenerations(result.data);
  }

  const deleteGeneration = (id) => {
    api.deleteGeneration(id).then(r => {
      fetchData();
    })
  }

  const createGeneration = (settings) => {
    console.log("default settings", settings)
    const prepSelections = () => {
      const options = document.querySelectorAll(".MuseNetOption");
      console.log("document", options)
      const updatedSettings = {}
      return(
        options.map(item => (
          updatedSettings[item.id] = item.value
        ))
      );
    }
    console.log("manipulated settings", prepSelections())
    // api.createGeneration(settings).then(r => {
    //   fetchData()
    // });
  }

  React.useEffect(() => {
    fetchData();
  }, [])

  return (
    <div className="App">
      <Generations
        generations={generations}
        deleteGeneration={deleteGeneration}
        />
      <OptionList
        options={options}
        createGeneration={createGeneration}
        />
    </div>
  );
}

const defaultValues = () => {
  let out = {}
  options.map(optSet => {
    optSet.children.map(item => {
      out[item.toParam] =  item.default;
    })
  })
  return out;
}

const OptionList = (props) => {
  const [settings, setSettings] = React.useState(defaultValues())

  const unwrap = (arr) => {
    return arr.map(child => {
      return (
      <div key={child.toParam}>
        {child.title}
        <InputSelector data={child} />
      </div>);
    })
  }

  let options = props.options.map(item => {
    return(
    <fieldset key={item.title}>
      <legend>{item.title}</legend>
      {unwrap(item.children)}
    </fieldset>)
  })

  return (
    <fieldset>
      <legend>Options</legend>
      <button onClick={() => props.createGeneration(settings)}>create</button>
      {options}
    </fieldset>
  );
}

const Generations = (props) => {
  return !props.generations || props.generations.map((item, index) => {
    return (
      <div key={index}>
        {item.composer} | {item.id} <button onClick={() => props.deleteGeneration(item["_id"]["$oid"])}>X</button>
      </div>);
  })
}

