import usePromise from "usePromise"
import { Midi } from '@tonejs/midi'
import * as Tone from 'tone'

const MIDIPlayer = (props) => {
  const [currentMidi, error, state] = usePromise(Midi.fromUrl(props.filename))
  let playing = false;
  const synths = [];
  const play = (e) => {
    playing = !playing
    console.log(">>>", playing)
					if (playing && currentMidi) {
						const now = Tone.now() + 0.5;
						currentMidi.tracks.forEach((track) => {
							//create a synth for each track
							const synth = new Tone.PolySynth(Tone.Synth, {
								envelope: {
									attack: 0.02,
									decay: 0.1,
									sustain: 0.3,
									release: 1,
								},
							}).toDestination();
							synths.push(synth);
							//schedule all of the events
							track.notes.forEach((note) => {
                if(note.duration > 0){
  								synth.triggerAttackRelease(
  									note.name,
  									note.duration,
  									note.time + now,
  									note.velocity
  								);
                  }
							});
						});
					} else {
						//dispose the synth and make a new one
						while (synths.length) {
							const synth = synths.shift();
							synth.disconnect();
						}
					}
  }
  return currentMidi ? <button onClick={(e) => play(e)}>Play</button> : "loading"
}

export const MIDIer = (props) => {
  const [result, error, state] = usePromise(loadMidi(props.item, false))
  if(result){
    if(result.data[0]){
      return <MIDIPlayer filename={result.data[0].midi_location} />
    } else {
      return <>loading...</>
    }
  } else {
    return <>uh oh...</>
  }
}
