const BASE_URI = 'http://localhost:4433';

class APIClient {
  constructor(accessToken) {
    this.accessToken = accessToken;
  }

  createGeneration(repo) {
    return this.perform('post', '/generations', repo);
  }

  deleteGeneration(repo) {
    return this.perform('delete', `/generations/${repo.id}`);
  }

  getGenerations() {
    return this.perform('get', '/generations');
  }

  getGeneration(repo) {
    return this.perform('get', `/generations/${repo.id}`);
  }

  async perform (method, resource, data) {
    return client({
      method,
      url: resource,
      data,
      headers: {
        Authorization: `Bearer ${this.accessToken}`
      }
    }).then(resp => {
      return resp.data ? resp.data : [];
    })
  }
}

export default APIClient;
