import "./daw.css"
import {keymaps} from "./keymaps"
import React from 'react'






const beatsPerMeasure = 4;
const dawResolution = 40; //measured in seconds

// const BPMtoSPB = (BPM) => {
//   60s/120b
// }

export const SongEditor = (props) => {
  const [bpm, setBPM] = React.useState(120)
  const [dawResolution, setDawResolution] = React.useState(40)

  if(props.current){
    let tracks = props.current.data
    return <div className="grid-container">
    <div>Logo</div>
    <div><Settings
            setBPM={setBPM} bpm={bpm}
            setDawResolution={setDawResolution} dawResolution={dawResolution}/></div>
    <div>Sidebar</div>
    <div><TrackViewer
              tracks={tracks}
              bpm={bpm}
              dawResolution={dawResolution} /></div>
    </div>
  } else {
    return "no parents"
  }
}

const Settings = (props) => {
  return <>
    <div><input type="range" min={50} max={150} value={props.bpm} onChange={(e) => props.setBPM(e.target.value)} /> {props.bpm}</div>
    <div><input type="range" min={30} max={100} value={props.dawResolution} onChange={(e) => props.setDawResolution(e.target.value)} /> {props.dawResolution}</div>
  </>
}

const TrackViewer = (props) => {
  const [activeTrack, setActiveTrack] = React.useState(0)
  let tracks = props.tracks.map((item, idx) => {
    return <div key={idx} onClick={() => setActiveTrack(idx)}>
        {item.instrument} | {item.notes.length}
        </div>//.notes.length
  })

  return <>
    <div className="mini-area">{tracks}</div>
    <div className="active-area">
      <ActiveArea
          active={props.tracks[activeTrack]}
          bpm={props.bpm}
          dawResolution={props.dawResolution}/>
    </div>
  </>
}

const ActiveArea = (props) => {
  if(!props.active || !props.active.notes || props.active.notes.length === 0) {return "Select a track"}
  let totalLengthInSeconds = 5
  let numBoxes = Math.round(totalLengthInSeconds*props.bpm/60);
  let boxes = Array.from(Array(numBoxes).keys())
  let notes = [...props.active.notes]//.splice(0, 15) //#FIXME
  let minNote = notes.reduce((min, b) => Math.min(min, b.pitch), notes[0].pitch);
  let maxNote = notes.reduce((min, b) => Math.max(min, b.pitch), notes[0].pitch);
  let filteredKeys = Object.keys(keymaps).filter((item) => (item >= minNote && item <= maxNote))

  let header = <div className="key-row">
                  <div className="accidental keyNote"></div>
                    {boxes.map((item, idx) => {
                      return <div className="daw-cell" key={idx}>{idx}</div>
                    })}
                </div>

  let body = filteredKeys.map((item, idx) => {
      let b = boxes.map((item, idx) => {
        return <div
                  className={["background-cell", "daw-cell"].join(" ")}
                  key={"beats"+idx}
                  style={{minWidth:`${dawResolution}px`}}></div>
      })
      return <div className="key-row" key={idx}>
        {b}
      </div>
  })

  let pianoKeys = filteredKeys.map((item, idx) => {
    let c = keymaps[item].includes("#") ? "accidental" : "natural";
    return <div className={[c, "keyNote"].join(" ")} key={idx}>{keymaps[item]}</div>
  }).reverse()

  return <>
            <div>
              {header}
            </div>
            <div className="daw">
              <div>
                {pianoKeys}
              </div>
              <div style={{position: "relative"}}>
                {body}
                <Notes
                  minNote={minNote}
                  notes={notes}
                  offset={maxNote}
                  tempo={props.bpm}
                  dawResolution={props.dawResolution}/>
              </div>
            </div>
        </>
  // return notes.map((item, idx) => {
  //   return <div key={idx}>{[keymaps[item.pitch], item.duration, item.time_on].join("|")}</div>
  // })
}

const Notes = (props) => {
  return props.notes.map((note, idx) => {
    return <Note {...props} note={note} key={idx} />
  })
}

const Note = (props) => {
  let unitCellWidth = 40;
  let unitCellHeight=30;

  let cellHeight = unitCellHeight;
  let cellWidth=(props.tempo*props.note.duration/60)*unitCellWidth;
  let topOffset=unitCellHeight*(props.offset - props.note.pitch);
  let leftOffset=props.tempo*(props.note.time_on)/60*unitCellWidth;

  let style = {
    position: "absolute",
    height:`${cellHeight}px`,
    width:`${cellWidth}px`,
    top:`${topOffset}px`,
    left:`${leftOffset}px`
  }

  return <div className="note" style={style}></div>
}
