import APIClient from './apiClient'
import React from 'react'
import {options} from "./Selection/MuseNetOptions"

let api = new APIClient("accessToken");

function App() {
  const [generations, setGenerations] = React.useState(null)
  const fetchData = async () => {
    const result = await api.generations();
    setGenerations(result.data);
  }

  const deleteGeneration = (id) => {
    api.deleteGeneration(id).then(r => {
      fetchData();
    })
  }

  const createGeneration = () => {
    let id = parseInt(document.getElementById("foobar").value)
    api.createGeneration({id}).then(r => {
      fetchData()
    });
  }

  React.useEffect(() => {
    fetchData();
  }, [])

  return (
    <div className="App">
      <Generations
        generations={generations}
        deleteGeneration={deleteGeneration}
        />
      <OptionList options={options} createGeneration={createGeneration} />
    </div>
  );
}

const defaultValues = () => {
  let out = {}
  options.map(optSet => {
    optSet.children.map(item => {
      out[item.toParam] =  item.default;
    })
  })
  return out;
}

const OptionList = (props) => {
  const [settings, setSettings] = React.useState(defaultValues())

  const unwrap = (arr) => {
    return arr.map(child => {
      return (
      <div key={child.toParam}>
        {child.toParam} | {settings[child.toParam]}
      </div>);
    })
  }

  let options = props.options.map(item => {
    return(
    <fieldset key={item.title}>
      <legend>{item.title}</legend>
      {unwrap(item.children)}
    </fieldset>)
  })

  return (
    <fieldset>
      <legend>Options</legend>
      <NewGenSettings
        createGeneration={props.createGeneration}
        />
      {options}
    </fieldset>
  );
}

const Generations = (props) => {
  return !props.generations || props.generations.map((item, index) => {
    return (
      <div key={index}>
        {item.composer} | {item.id} <button onClick={() => props.deleteGeneration(item.id)}>X</button>
      </div>);
  })
}

const NewGenSettings = (props) => {
  const [value, setValue] = React.useState("2");
  const handleChange = (event) => {
    setValue(event.target.value);
  };

  return(
    <fieldset style={{background: "rgba(150, 50, 15, .3)"}}>
      <legend>new gen</legend>
      <div><input id="foobar" value={value} onChange={handleChange}/></div>
      <ComposerSettings />
      <button onClick={props.createGeneration}>create</button>
    </fieldset>
  )
}

const ComposerSettings = (props) => {
  const [value, setValue] = React.useState("mozart");
  const handleChange = (event) => {
    setValue(event.target.value);
  };

  return (
    <div>
      <select
        value={value}
        onChange={handleChange}
        >
        <option value={"mozart"}>Mozart</option>
        <option value={"chopin"}>Chopin</option>
      </select>
    </div>
  );
}

export default App;
