import React from 'react';

const SliderInput = (props) => {
  const data = props.data
  const step_size = data.max < 3 ? .01 : 1;
  const [value, setValue] = React.useState(data.default)

  function handleChange(e){
    setValue(e.target.value)
  }

  return (
    <>
      <input
        id={data.toParam}
        type="range"
        min={data.min}
        max={data.max}
        value={data.defaultValue}
        step={step_size}
        onChange={handleChange}/>
      {value}
    </>
  );
}

const Checkbox = (props) =>{
  return(
    <input
      type="checkbox"
      id={props.data}
      value={props.data}
      checked={props.checked}
      onClick={props.onClick}
      />
  );
}

const Checkboxes = (props) => {
  const [values, setValues] = React.useState(props.data.default)

  function handleClick(e){
    let target = e.target.value;
    let newValues = values.includes(target)
                      ? values.filter(i => i !== target)
                      : [...values, target]
    setValues([...newValues])
  }

  return props.data.options.map(item => <Checkbox
    data={item}
    checked={values.includes(item)}
    onClick={handleClick}
    />);
}

// arr.includes(item)
//     ? arr.filter(i => i !== item) // remove item
//     : [ ...arr, item ]; // add item

export const InputSelector = (props) => {
  const data = props.data
  const choice = data.inputType;

  if(choice === "select"){
    let options = data.options.map(item => <option value={item}>{item}</option> )
    return (
      <select id={data.toParam}>
        {options}
      </select>
    );
  } else if (choice === "slider"){
    return (
      <SliderInput data={data} />
    );
  } else if (choice === "checkboxinput"){
    return <Checkboxes data={data} />
  } else {
    return failed("failure")
  }
}

const failed = (text) => <>{text}</>;
