import React from 'react';

const Notes = (props) => {
  let mainTrack = props.tracks[props.activeTrack].notes.map((note, idx) => {
    return <Note {...props} note={note} key={idx} />
  })

  let backgroundTracks = [...props.tracks]
  backgroundTracks.splice(props.activeTrack, 1)
  backgroundTracks = props.allVisible
      ? backgroundTracks.map((track, trackIndex) => {
        return track.notes.map((note, noteIndex) => {
          return <Note {...props} note={note} key={trackIndex+"-"+noteIndex} inactive={true} color={COLORS[trackIndex]}/>
        })
      })
      : <></>;

  return <>{mainTrack} {backgroundTracks}</>
}

const Note = (props) => {
  let unitCellHeight=30;

  let cellHeight = unitCellHeight;
  let cellWidth=(props.bpm*props.note.duration/60)*props.dawResolution;
  let topOffset=unitCellHeight*(props.offset - props.note.pitch);
  let leftOffset=props.bpm*(props.note.time_on)/60*props.dawResolution;

  let style = {
    position: "absolute",
    height:`${cellHeight}px`,
    width:`${cellWidth}px`,
    top:`${topOffset}px`,
    left:`${leftOffset}px`
  }

  // console.log("sstlye", style)

  if(props.inactive){
    style.backgroundColor = props.color;
  } else {
    style.backgroundColor = "blue"
  }

  return <div className="note" style={style}></div>
}
