import "./daw.css"
import {keymaps} from "./keymaps"
import React from 'react'

const bpm = 60/.13;
const beatsPerMeasure = 4;
const dawResolution = .26

// const BPMtoSPB = (BPM) => {
//   60s/120b
// }

export const SongEditor = (props) => {
  if(props.current){
    let tracks = props.current.data
    return <div className="grid-container">
    <div>Logo</div>
    <div>Settings</div>
    <div>Sidebar</div>
    <div><TrackViewer tracks={tracks} /></div>
    </div>
  } else {
    return "no parents"
  }
}

const TrackViewer = (props) => {
  const [activeTrack, setActiveTrack] = React.useState(0)
  let tracks = props.tracks.map((item, idx) => {
    console.log("tracking")
    return <div key={idx} onClick={() => setActiveTrack(idx)}>
        {item.instrument} | {item.notes.length}
        </div>//.notes.length
  })

  return <>
  <div className="mini-area">{tracks}</div>
  <div className="active-area"><ActiveArea active={props.tracks[activeTrack]} /></div>
  </>
}

const ActiveArea = (props) => {
  if(!props.active || !props.active.notes || props.active.notes.length === 0) {return "Select a track"}
  let totalLengthInSeconds = 5
  let numBoxes = Math.round(totalLengthInSeconds/dawResolution);
  let boxes = Array.from(Array(numBoxes).keys())
  let notes = [...props.active.notes].splice(0, 5) //#FIXME
  let minNote = notes.reduce((min, b) => Math.min(min, b.pitch), notes[0].pitch);
  let maxNote = notes.reduce((min, b) => Math.max(min, b.pitch), notes[0].pitch);
  let filteredKeys = Object.keys(keymaps).filter((item) => (item >= minNote && item <= maxNote))

  let header = <div className="key-row">
                  <div className="accidental keyNote"></div>
                    {boxes.map((item, idx) => {
                      return <div className="daw-cell" key={idx}>{(dawResolution * idx / secsPerBeat).toFixed(2)}</div>
                    })}
                </div>

  let body = filteredKeys.map((item, idx) => {
      let b = boxes.map((item, idx) => {
        // let cN = (item + 1) % beatsPerMeasure ? "downbeat" : "beat";
        return <div className={["background-cell", "daw-cell"].join(" ")} key={"beats"+idx}></div>
      })
      return <div className="key-row" key={idx}>
        {b}
      </div>
  })

  let pianoKeys = filteredKeys.map((item, idx) => {
    let c = keymaps[item].includes("#") ? "accidental" : "natural";
    return <div className={[c, "keyNote"].join(" ")} key={idx}>{keymaps[item]}</div>
  })

  return <>
            <div>
              {header}
            </div>
            <div className="daw">
              <div>
                {pianoKeys}
              </div>
              <div style={{position: "relative"}}>
                {body}
                <Downbeats
                  beatsPerMeasure={beatsPerMeasure}
                  secsPerBeat={secsPerBeat}
                  dawResolution={dawResolution}
                  />
                <Notes
                  minNote={minNote}
                  notes={notes}
                  offset={minNote}
                  tempo={secsPerBeat}
                  dawResolution={dawResolution}/>
              </div>
            </div>
        </>
  // return notes.map((item, idx) => {
  //   return <div key={idx}>{[keymaps[item.pitch], item.duration, item.time_on].join("|")}</div>
  // })
}

const Downbeats = (props) => {
  const downbeatWidth=3;
  return ""
  // props.boxes.map((item, index) => {
  //
  // })
}

const Notes = (props) => {
  return props.notes.map((note, idx) => {
    return <Note {...props} note={note} key={idx} />
  })
}

const Note = (props) => {
  let cellHeight=30;
  let cellWidth=40;
  let topOffset=cellHeight*(props.note.pitch - props.offset);
  let leftOffset=props.note.time_on;

  console.log(props.note)
  let style = {
    position: "absolute",
    height:`${cellHeight}px`,
    width:`${cellWidth}px`,
    top:`${topOffset}px`,
    left:`${leftOffset}px`
  }
  console.log(style)
  return <div className="note" style={style}></div>
}
