import APIClient from './apiClient'
import React from 'react'
import {options} from "Selection/MuseNetOptions"
import {InputSelector} from "Selection/Inputs"

let api = new APIClient("accessToken");

function App() {
  const [generations, setGenerations] = React.useState(null)
  const [parent, setParent] = React.useState(null)

  const fetchData = async () => {
    const result = await api.generations();
    setGenerations(result.data);
  }

  const deleteGeneration = (id) => {
    api.deleteGeneration(id).then(r => {
      fetchData();
    })
  }

  const createGeneration = () => {
    const prepSelections = () => {
      const options = document.querySelectorAll(".MuseNetOption");
      const updatedSettings = {}
      options.forEach(item => {
        updatedSettings[item.id] = item.type==="checkbox"
                                      ? item.checked
                                      : item.value
      })
      updatedSettings.parent_enc = ""
      updatedSettings.parent_id = "132122"
      return updatedSettings;
    }

    api.createGeneration(prepSelections()).then(r => {
      fetchData()
    });
  }

  React.useEffect(() => {
    fetchData();
  }, [])

  return (
    <div className="App">
      <Generations
        generations={generations}
        deleteGeneration={deleteGeneration}
        setParent={setParent}
        />
      <OptionList
        options={options}
        createGeneration={createGeneration}
        parent={parent}
        />
    </div>
  );
}

const ParentOptions = (props) => {
  if(!props.parent) {return <>{JSON.stringify(pros)}</>}
  return (
    <>parent options</>
  );
}

const OptionList = (props) => {
  const unwrap = (arr) => {
    return arr.map(child => {
      return (
      <div key={child.toParam}>
        {child.title}
        <InputSelector data={child} />
      </div>);
    })
  }

  let options = props.options.map(item => {
    return(
    <fieldset key={item.title}>
      <legend>{item.title}</legend>
      {unwrap(item.children)}
    </fieldset>)
  })

  return (
    <fieldset>
      <legend>Parent Options</legend>
      <button onClick={() => props.createGeneration()}>create</button>
      <ParentOptions parents={props.parent} />
      {options}
    </fieldset>
  );
}

const Generations = (props) => {
  return !props.generations || props.generations.map((item, index) => {
    return (
      <div key={index}>
        {item.composer} | {<button onClick={() => props.setParent(item["_id"]["$oid"])}>parent</button>} <button onClick={() => props.deleteGeneration(item["_id"]["$oid"])}>X</button>
      </div>);
  })
}

export default App;
