import {reduceNotes} from "./utils"

const calculateScore = (data) => {
  let interval = (60/data.tempo)/data.subdivision;
  let distance = (i) => Math.abs(Math.round(i) - i);
  let startTimes = reduceNotes(data.tracks).map(item => distance(item.time_on/interval));

  return startTimes.reduce((a, b) => a + b, 0)
}

export const Score = (props) => {
  return JSON.stringify(calculateScore({
    tempo: props.bpm,
    subdivision: props.subdivision,
    tracks: props.tracks,
  }))
}

export const autoScore = (tracks) => {
  console.log("scoring")
  let tempo = 60;
  let subdivision = 2;
  let results = {best: 100000000};
  do{
    console.log("tempo loop")
    subdivision = 2;
    do{
      console.log("sub loop")
      let score = calculateScore({
        tempo: tempo,
        subdivision: subdivision,
        tracks: tracks
      })

      if(score < results.best){
        results = {
          best: score,
          tempo: tempo,
          subdivision: subdivision,
        }
      }

      subdivision += 1;
    } while(subdivision < 5)
    tempo += .05
    console.log("tempo:", tempo)
  } while(tempo < 200)

  return results
}
