import APIClient from './apiClient'
import React from 'react'
import {options} from "Selection/MuseNetOptions"
import {InputSelector} from "Selection/Inputs"
import usePromise from "usePromise"
import { Midi } from '@tonejs/midi'

let api = new APIClient("accessToken");

function App() {
  const [generations, setGenerations] = React.useState(null)
  const [parentData, setParentData] = React.useState(null)

  const fetchParentData = async (parentId) => {
    console.log("updating parent data")
    let data = await api.getGeneration(parentId)
    setParentData(data.data);
  }

  const fetchData = async () => {
    const result = await api.generations();
    setGenerations(result.data);
  }

  const deleteGeneration = (id) => {
    api.deleteGeneration(id).then(r => {
      fetchData();
    })
  }

  const createGeneration = () => {
    const prepSelections = () => {
      const selectedOptions = document.querySelectorAll(".MuseNetOption");
      const updatedSettings = {}
      selectedOptions.forEach(item => {
        updatedSettings[item.id] = item.type==="checkbox"
                                      ? item.checked
                                      : item.value
      })
      updatedSettings.parent_enc = document.getElementById("parentEnc").value
      updatedSettings.parent_id = document.getElementById("parentId").value
      return updatedSettings;
    }

    api.createGeneration(prepSelections()).then(r => {
      fetchData()
    });
  }

  React.useEffect(() => {
    fetchData();
  }, [])

  return (
    <div className="App">
      <Generations
        generations={generations}
        deleteGeneration={deleteGeneration}
        setParentData={fetchParentData}
        />
      <OptionList
        options={options}
        createGeneration={createGeneration}
        parent={parentData}
        />
    </div>
  );
}

const ParentOptions = (props) => {
  let enc = props.parent ? props.parent.enc : "";
  let pid = props.parent ? props.parent["_id"]["$oid"] : "";

  return (
    <fieldset>
      <legend>Parent</legend>
      <input
        type="hidden"
        id="parentEnc"
        value={enc}/>
      <input
        type="hidden"
        id="parentId"
        value={pid}/>
      parent: {pid}
    </fieldset>
  );
}

const OptionList = (props) => {
  const unwrap = (arr) => {
    return arr.map(child => {
      return (
      <div key={child.toParam}>
        {child.title}
        <InputSelector data={child} parent={props.parent}/>
      </div>);
    })
  }

  let options = props.options.map(item => {
    return(
    <fieldset key={item.title}>
      <legend>{item.title}</legend>
      {unwrap(item.children)}
    </fieldset>)
  })

  return (
    <fieldset>
      <legend>Parent Options</legend>
      <button onClick={() => props.createGeneration()}>create</button>
      <ParentOptions parent={props.parent} />
      {options}
    </fieldset>
  );
}

const loadMidi = (item, overwrite) => {
  return api.getMediaForGeneration(item, overwrite)
}

const MIDIPlayer = (props) => {
  let p = api.BASE_URI + "/midifiles/"+props.filename
  const [result, error, state] = usePromise(Midi.fromUrl(p))
  const play = () => {
    // console.log("results", p, result)
  }
  return result ? <button onClick={() => play}>Play</button> : "loading"
}

const MIDI = (props) => {
  const [result, error, state] = usePromise(loadMidi(props.item, false))
  if(result){
    if(result.data[0]){
      return <MIDIPlayer filename={result.data[0].midi_location} />
    } else {
      return <>loading...</>
    }
  } else {
    return <>uh oh...</>
  }
}

const Generations = (props) => {
  return !props.generations || props.generations.map((item, index) => {
    return (
      <div key={index}>
        {item.composer} | {item.parent_id} | <MIDI item={item} />
        <button onClick={() => props.setParentData(item["_id"]["$oid"])}>parent</button>
        <button onClick={() => props.deleteGeneration(item["_id"]["$oid"])}>X</button>
      </div>);
  })
}

export default App;
